INSERT INTO `animaux` (`id`, `nom`, `description`, `enclos_id`, `photo`) VALUES
(1, 'Autruche', 'Plus grand oiseau du monde, originaire d\'Afrique. L\'autruche est incapable de voler, mais peut courir à des vitesses impressionnantes, se nourrissant principalement de plantes et parfois d\'insectes.\r\n', 16, 'https://i.pinimg.com/736x/f2/1f/74/f21f748018c87e6a748c28986f518d94.jpg'),
(2, 'Gazelle', 'Mammifère gracieux des savanes et des déserts, connu pour sa vitesse et son agilité. La gazelle est herbivore, se nourrissant d\'herbes, de feuilles et de fruits.\r\n', 16, 'https://i.pinimg.com/736x/6a/2e/c2/6a2ec2373c78196c291eb8ab65a42e8a.jpg'),
(3, 'Guépard', 'Mammifère carnivore et le plus rapide des animaux terrestres, vivant dans les savanes d\'Afrique et d\'Iran. Le guépard chasse principalement de petits herbivores.\r\n', 17, 'https://i.pinimg.com/474x/b0/44/40/b04440052e87804c9abc33bc2b24ab2d.jpg'),
(4, 'Gnou', 'Herbivore robuste des plaines africaines, connu pour ses migrations massives. Le gnou se nourrit principalement d\'herbe.\r\n', 12, 'https://www.monde-animal.fr/wp-content/uploads/2020/04/fiche-animale-monde-animal-gnou-noir.jpg'),
(5, 'Oryx Beisa', 'Antilope africaine avec de longues cornes droites, adaptée aux climats désertiques. Herbivore, il se nourrit de feuilles et d\'herbes.\r\n', 12, 'https://upload.wikimedia.org/wikipedia/commons/thumb/a/a9/Oryx_gazella_beisa.jpg/800px-Oryx_gazella_beisa.jpg'),
(6, 'Rhinocéros', 'Grand herbivore à peau épaisse et corne distinctive, vivant en Afrique et en Asie. Il se nourrit principalement d\'herbes, de branches et de feuillages.\r\n', 12, 'https://th.bing.com/th/id/R.28083922732efc361c2885bc5795ad41?rik=P1GWU5vUtKXFFA&pid=ImgRaw&r=0'),
(7, 'Suricate', 'Petit mammifère carnivore vivant dans les déserts d\'Afrique australe, connu pour ses postures dressées et sa vie en groupe.\r\n', 13, 'data:image/jpeg;base64,'),
(8, 'Fennec', 'Petit renard du désert, vivant en Afrique du Nord, caractérisé par ses grandes oreilles. Il est omnivore, se nourrissant de plantes, d\'insectes et de petits animaux.\r\n', 14, 'data:image/jpeg;base64,'),
(9, 'Coati', 'Mammifère omnivore d\'Amérique centrale et du Sud, apparenté au raton laveur. Le coati est actif le jour et se nourrit de fruits, insectes et petits animaux.\r\n', 15, 'data:image/jpeg;base64,'),
(10, 'Saimiri', 'Singe d\'Amérique centrale et du Sud, souvent appelé \"singe écureuil\". Le saimiri est omnivore et vit en groupe, se nourrissant de fruits, insectes et petits animaux.\r\n', 15, 'data:image/jpeg;base64,');
INSERT INTO `animaux` (`id`, `nom`, `description`, `enclos_id`, `photo`) VALUES
(11, 'Tapir', 'Grand herbivore d\'Amérique centrale et du Sud, avec un nez allongé. Le tapir se nourrit de feuilles, de fruits et de plantes aquatiques.\r\n', 18, 'data:image/jpeg;base64,'),
(12, 'Casoar', 'Grand oiseau d\'Australie et de Nouvelle-Guinée, connu pour sa crête casque et son tempérament défensif. Le casoar est principalement frugivore.\r\n\r\n', 19, 'data:image/jpeg;base64,'),
(13, 'Crocodile Nain', 'Plus petit des crocodiles, vivant en Afrique de l\'Ouest, se nourrissant de poissons, amphibiens et petits mammifères.\r\n', 20, 'data:image/jpeg;base64,');
