INSERT INTO `animaux` (`id`, `nom`, `description`, `enclos_id`, `photo`) VALUES
(60, 'Bison', 'Grand herbivore robuste, vivant généralement en troupeaux, adapté aux prairies et plaines.', 38, 'data:image/jpeg;base64,'),
(61, 'Ane de Provence', ' Une sous-espèce d\'âne, reconnue pour sa rusticité et souvent utilisée dans les régions rurales.\r\n', 48, 'data:image/jpeg;base64,'),
